<?php get_template_part( 'header', 'soon' ); ?><?php$logo        = smarket_option( '404_logo', '' );$footer_404  = smarket_option( 'footer_404', '' );$background  = smarket_option( '404_background', '' );$title       = smarket_option( 'title', '404' );$subtitle    = smarket_option( 'subtitle', 'This\'s Not The Web Page You\'re Looking For' );$args        = array(	'a'      => array(		'href'  => array(),		'title' => array(),	),	'br'     => array(),	'em'     => array(),	'strong' => array(),);$subtitles   = wp_kses( $subtitle, $args );$footers_404 = wp_kses( $footer_404, $args );if ( $logo ) {	$logo_url = $logo[ 'url' ];} else {	$logo_url = get_template_directory_uri() . '/images/logo.png';}?>    <div class="page-maintenance page-404"         style="background-image: url(<?php echo esc_url( $background[ 'url' ] ); ?>);">        <div class="logo-maintenance logo-404">            <a href="<?php echo esc_url( get_home_url() ); ?>">                <img src="<?php echo esc_url( $logo_url ); ?>" alt="<?php the_title(); ?>">            </a>        </div>        <div class="content-maintenance">            <h1 class="main-title"><?php echo esc_html( $title ); ?></h1>            <h2 class="sub-title"><?php echo balanceTags( $subtitles ); ?></h2>            <div class="des">                <span><?php echo esc_html__( 'please try one of the following pages', 'smarket' ) ?></span>                <a class="button"                   href="<?php echo esc_url( get_home_url() ); ?>"><?php esc_html_e( 'home page', 'smarket' ); ?></a>            </div>			<?php get_search_form(); ?>        </div>        <div class="footer-maintenance footer-404">			<?php echo balanceTags( $footers_404 ); ?>        </div>    </div><?php get_template_part( 'footer', 'soon' ); ?>
