<?php/** * The template for displaying comments * * The area of the page that contains both current comments * and the comment form. * * @package WordPress * @subpackage Smarket * @since Smarket 1.0 *//* * If the current post is protected by a password and * the visitor has not yet entered the password we will * return early without loading the comments. */if ( post_password_required() ) {	return;}$comment_field = '<p class="comment-reply-content"><label>' . esc_html__( 'Comment', 'smarket' ) . ' <span class="required">*</span></label><textarea class="input-form" id="comment" name="comment" cols="45" rows="6" aria-required="true">' .	'</textarea></p>';$fields        = array(	'name'    => '<p class="comment-reply-content"><label>' . esc_html__( 'Name', 'smarket' ) . ' <span class="required">*</span></label><input type="text" name="author" id="name" class="input-form name" /></p>',	'email'   => '<p class="comment-reply-content"><label>' . esc_html__( 'Email', 'smarket' ) . ' <span class="required">*</span></label><input type="text" name="email" id="email" class="input-form email" /></p>',	'website' => '<p class="comment-reply-content"><label>' . esc_html__( 'Website', 'smarket' ) . ' </label><input type="text" name="website" id="website" class="input-form website" /></p>',);$comment_form_args = array(	'class_submit'  => 'button',	'comment_field' => $comment_field,	'fields'        => $fields,	'label_submit'  => esc_html__('submit','smarket'),);?><div id="comments" class="post-comments">	<?php if ( have_comments() ) : ?>        <h4 class="comments-title"><?php echo esc_html__( 'COMMENTS', 'smarket' ) ?><?php echo '<span>(' . get_comments_number() . ')</span>'; ?></h4>		<?php smarket_comment_nav(); ?>        <ol class="comments">			<?php			wp_list_comments( array(				'style'       => 'ol',				'short_ping'  => true,				'avatar_size' => 68,				'callback'    => 'smarket_custom_comment',			)			);			?>        </ol><!-- .comment-list -->		<?php smarket_comment_nav(); ?>	<?php endif; // have_comments() ?>	<?php	// If comments are closed and there are comments, let's leave a little note, shall we?	if ( !comments_open() && get_comments_number() && post_type_supports( get_post_type(), 'comments' ) ) :		?>        <p class="no-comments"><?php echo esc_html__( 'Comments are closed.', 'smarket' ); ?></p>	<?php endif; ?>	<?php comment_form( $comment_form_args ); ?></div><!-- .comments-area -->