<?php get_header();?><?php/*Default  page layout*/$smarket_page_extra_class = smarket_get_post_meta(get_the_ID(),'smarket_page_extra_class','');$smarket_page_layout = smarket_get_post_meta(get_the_ID(),'smarket_page_layout','left');/*Main container class*/$smarket_main_container_class = array();$smarket_main_container_class[] = $smarket_page_extra_class;$smarket_main_container_class[] = 'main-container';if( $smarket_page_layout == 'full'){    $smarket_main_container_class[] = 'no-sidebar';}else{    $smarket_main_container_class[] = $smarket_page_layout.'-slidebar';}$smarket_main_content_class = array();$smarket_main_content_class[] = 'main-content';if( $smarket_page_layout == 'full' ){    $smarket_main_content_class[] ='col-sm-12';}else{    $smarket_main_content_class[] = 'col-md-9 col-sm-8';}$smarket_slidebar_class = array();$smarket_slidebar_class[] = 'sidebar';if( $smarket_page_layout != 'full'){    $smarket_slidebar_class[] = 'col-md-3 col-sm-4';}?><main class="site-main <?php echo esc_attr( implode(' ', $smarket_main_container_class) );?>">    <?php get_template_part('template_parts/part','breadcrumb');?>	<?php get_template_part('template_parts/page','banner');?>    <div class="container">        <div class="row">            <div class="<?php echo esc_attr( implode(' ', $smarket_main_content_class) );?>">                <?php                if( have_posts()){                    while( have_posts()){                        the_post();                        ?>                        <div class="page-main-content">                            <div class="page-title">								<?php the_title();?>                            </div>                            <?php                            the_content();                            wp_link_pages( array(                                'before'      => '<div class="page-links"><span class="page-links-title">' . esc_html__( 'Pages:', 'smarket' ) . '</span>',                                'after'       => '</div>',                                'link_before' => '<span>',                                'link_after'  => '</span>',                                'pagelink'    => '<span class="screen-reader-text">' . esc_html__( 'Page', 'smarket' ) . ' </span>%',                                'separator'   => '<span class="screen-reader-text">, </span>',                            ) );                            ?>                        </div>                        <?php                        // If comments are open or we have at least one comment, load up the comment template.                        if ( comments_open() || get_comments_number() ) :                            comments_template();                        endif;                        ?>                        <?php                    }                }                ?>            </div>            <?php if( $smarket_page_layout != "full" ):?>                <div class="<?php echo esc_attr( implode(' ', $smarket_slidebar_class) );?>">                    <?php get_sidebar('page');?>                </div>            <?php endif;?>        </div>    </div></main><?php get_footer();?>