<?php$smarket_blog_placehold    = smarket_option( 'smarket_blog_placehold', 'no' );$smarket_blog_list_style   = smarket_option( 'smarket_blog_list_style', 'list' );$smarket_blog_full_content = smarket_option( 'smarket_blog_full_content', 1 );if ( have_posts() ) {	?>    <div class="post-list post-items">		<?php		while ( have_posts() ) {			the_post();			?>            <div <?php post_class( 'post-item' ); ?>>                <div class="post-item-info">                    <h3 class="post-name">                        <a href="<?php the_permalink(); ?>"><?php the_title(); ?></a>                    </h3>                    <div class="post-metas">                        <span class="author">                            <img src="<?php echo get_template_directory_uri() . '/images/user.png' ?>" alt="">                            <?php esc_html_e( 'By', 'smarket' ); ?> <?php the_author(); ?>                        </span>                            <span class="time">                            <img src="<?php echo get_template_directory_uri() . '/images/calendar.png' ?>" alt="">                                <?php echo get_the_date(); ?>                        </span>                            <span class="cat">                            <img src="<?php echo get_template_directory_uri() . '/images/tag.png' ?>" alt="">                                <?php the_category( ', ', '' ); ?>                        </span>                        <span class="comment">                            <img src="<?php echo get_template_directory_uri() . '/images/comment.png' ?>" alt="">                                <?php                                $comment = get_comments_number();                                $number  = zeroise( $comment, 2 );                                if ( $comment == 0 ) {                                    printf( '%s', esc_html__( '0 Comment', 'smarket' ) );                                } elseif ( $comment < 2 ) {                                    printf( '%s %s', $number, esc_html__( 'Comment', 'smarket' ) );                                } else {                                    printf( '%s %s', $number, esc_html__( 'Comments', 'smarket' ) );                                }                                ?>                        </span>						<?php						if ( is_sticky() && is_home() && !is_paged() ) {							printf( '<span class="sticky-post"><i class="fa fa-flag"></i>%s</span>', esc_html__( 'Sticky', 'smarket' ) );						}						?>                    </div>					<?php if ( has_post_thumbnail() || $smarket_blog_placehold == 'yes' ): ?>                        <div class="post-thumb">							<?php smarket_post_thumbnail(); ?>                        </div>					<?php endif; ?>					<?php if ( $smarket_blog_full_content == 1 ) : ?>                        <div class="post-excerpt">                            <?php                            /* translators: %s: Name of current post */                            the_content( sprintf(								esc_html__( 'Continue reading %s', 'smarket' ),                                the_title( '<span class="screen-reader-text">', '</span>', false )                            ) );                            wp_link_pages( array(                                'before'      => '<div class="page-links"><span class="page-links-title">' . esc_html__( 'Pages:', 'smarket' ) . '</span>',                                'after'       => '</div>',                                'link_before' => '<span>',                                'link_after'  => '</span>',                                'pagelink'    => '<span class="screen-reader-text">' . esc_html__( 'Page', 'smarket' ) . ' </span>%',                                'separator'   => '<span class="screen-reader-text">, </span>',                            ) );                            ?>                        </div>					<?php else : ?>                        <div class="post-excerpt"><?php echo wp_trim_words( apply_filters( 'the_excerpt', get_the_excerpt() ), 100, esc_html__( '...', 'smarket' ) ); ?></div>                        <a href="<?php the_permalink(); ?>" class="button">                            <span class="text"><?php echo esc_html__( 'Read more', 'smarket' ); ?></span>                        </a>                    <?php endif; ?>                </div>            </div>			<?php		}		?>		<?php wp_reset_postdata(); ?>    </div>	<?php	smarket_paging_nav();} else {	get_template_part( 'content', 'none' );}