<?php$smarket_blog_placehold  = smarket_option('smarket_blog_placehold','no');?><div <?php post_class('post-item');?>>    <div class="post-header">        <h3 class="post-name">			<?php the_title();?>        </h3>        <div class="post-metas">            <span class="author">                <img src="<?php echo get_template_directory_uri() . '/images/user.png' ?>" alt="">                <?php esc_html_e('By:','smarket');?> <?php the_author();?>            </span>            <span class="time">                <img src="<?php echo get_template_directory_uri() . '/images/calendar.png' ?>" alt="">                <?php echo get_the_date();?>            </span>            <span class="cat">                <img src="<?php echo get_template_directory_uri() . '/images/tag.png' ?>" alt="">                <?php the_category(', ','');?>            </span>            <span class="comment">                <img src="<?php echo get_template_directory_uri() . '/images/comment.png' ?>" alt="">                <?php                $comment = get_comments_number();                $number = zeroise($comment,2);                if ($comment == 0){                    printf( '%s', esc_html__( '0 Comment', 'smarket' ) );                } elseif ($comment < 2){                    printf( '%s %s',$number, esc_html__( 'Comment', 'smarket' ) );                } else {                    printf( '%s %s',$number, esc_html__( 'Comments', 'smarket' ) );                }                ?>            </span>        </div>    </div>    <?php if( has_post_thumbnail() || $smarket_blog_placehold =='yes' ): ?>        <div class="post-thumb">            <?php the_post_thumbnail('full');?>        </div>    <?php endif;?>    <div class="post-detail">        <div class="post-content">            <?php the_content(); ?>			<?php			wp_link_pages( array(				'before'      => '<div class="page-links">',				'after'       => '</div>',				'link_before' => '<span>',				'link_after'  => '</span>',				'pagelink'    => '%',				'separator'   => '',			) );            ?>        </div>    </div>    <div class="post-footer">        <?php get_template_part('templates/blogs/blog','share'); ?>		<?php		the_post_navigation( array(			'prev_text' => '<span class="screen-reader-text"><i class="fa fa-caret-left" aria-hidden="true"></i>' . esc_html__( 'Previous', 'smarket' ) . '</span>',			'next_text' => '<span class="screen-reader-text">' . esc_html__( 'Next', 'smarket' ) . '<i class="fa fa-caret-right" aria-hidden="true"></i></span>',		) );        ?>        <?php get_template_part('templates/blogs/blog','bio');?>    </div></div>